import { useState } from "react";
import styles from "./Header.module.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { library } from "@fortawesome/fontawesome-svg-core";
import { fas } from "@fortawesome/free-solid-svg-icons";
import { faTwitter, faFontAwesome } from "@fortawesome/free-brands-svg-icons";
import { faHatCowboy } from "@fortawesome/pro-thin-svg-icons";
import { faHatChef } from "@fortawesome/sharp-solid-svg-icons";
import { faPlateUtensils } from "@fortawesome/sharp-regular-svg-icons";

function Header() {
  library.add(
    fas,
    faTwitter,
    faFontAwesome,
    faHatCowboy,
    faHatChef,
    faPlateUtensils
  );
  function onClickMenu() {
    setMenu((menu) => !menu);

    console.log(menu);
  }
  const [menu, setMenu] = useState(false);
  return (
    <nav className={styles.header}>
      <div className={styles.header__title}>
        <div>NETFLIX</div>
      </div>

      <ul className={styles.header__options}>
        <li>
          <a href="#">Romance</a>
        </li>
        <li>
          <a href="#">Thriller</a>
        </li>
        <li>
          <a href="#">Action</a>
        </li>
      </ul>

      <ul className={styles.header__about_us}>
        <li>twitter</li>
        <li>instagram</li>
      </ul>

      <a href="#" className={styles.header__menu} onClick={onClickMenu}>
        <FontAwesomeIcon icon="fa-sharp fa-solid fa-bars" />
      </a>
    </nav>
  );
}

export default Header;
